// ─── SHARED: INTERSECTION OBSERVER ─────────────────────────────
function useInView(options = {}) {
  const ref = useRef(null);
  const [isInView, setIsInView] = useState(false);
  useEffect(() => {
    const el = ref.current;
    if (!el) return;
    const obs = new IntersectionObserver(
      ([entry]) => { if (entry.isIntersecting) { setIsInView(true); obs.unobserve(el); } },
      { threshold: 0.12, ...options }
    );
    obs.observe(el);
    return () => obs.disconnect();
  }, []);
  return [ref, isInView];
}

function Reveal({ children, className = "", delay = 0, direction = "up" }) {
  const [ref, isInView] = useInView();
  const transforms = { up: "translateY(60px)", down: "translateY(-60px)", left: "translateX(80px)", right: "translateX(-80px)", none: "none" };
  return (
    <div ref={ref} className={className} style={{ opacity: isInView ? 1 : 0, transform: isInView ? "none" : transforms[direction], transition: `opacity 0.7s cubic-bezier(0.16,1,0.3,1) ${delay}s, transform 0.7s cubic-bezier(0.16,1,0.3,1) ${delay}s` }}>
      {children}
    </div>
  );
}

function NoiseOverlay() {
  return (
    <svg className="fixed inset-0 w-full h-full pointer-events-none z-50 opacity-[0.03]" style={{ mixBlendMode: "multiply" }}>
      <filter id="noise"><feTurbulence type="fractalNoise" baseFrequency="0.85" numOctaves="4" stitchTiles="stitch" /></filter>
      <rect width="100%" height="100%" filter="url(#noise)" />
    </svg>
  );
}

function ParallaxImg({ src, alt, className = "" }) {
  const ref = useRef(null);
  const [offset, setOffset] = useState(0);
  useEffect(() => {
    const handler = () => { if (!ref.current) return; const rect = ref.current.getBoundingClientRect(); setOffset((rect.top + rect.height / 2 - window.innerHeight / 2) * 0.08); };
    window.addEventListener("scroll", handler, { passive: true }); handler();
    return () => window.removeEventListener("scroll", handler);
  }, []);
  return (<div ref={ref} className={`overflow-hidden ${className}`}><img src={src} alt={alt} loading="lazy" className="w-full h-full object-cover" style={{ transform: `translateY(${offset}px) scale(1.1)` }} /></div>);
}

function Marquee({ text, speed = 30 }) {
  const items = Array(8).fill(text);
  return (
    <div className="overflow-hidden whitespace-nowrap border-y-2 border-stone-800 bg-stone-900 py-3 select-none">
      <div className="inline-flex" style={{ animation: `marquee ${speed}s linear infinite` }}>
        {items.map((t, i) => (<span key={i} className="mx-8 text-sm tracking-[0.3em] uppercase text-stone-400 font-mono">{t} <span className="text-amber-500 mx-4">◆</span></span>))}
      </div>
      <style>{`@keyframes marquee { 0% { transform: translateX(0); } 100% { transform: translateX(-50%); } }`}</style>
    </div>
  );
}

