    setMenuOpen(false);
    if (p !== page) { setPage(p); setTimeout(() => { if (hash) { const el = document.querySelector(hash); if (el) el.scrollIntoView({ behavior: "smooth" }); } else { window.scrollTo({ top: 0, behavior: "smooth" }); } }, 100); }
    else if (hash) { const el = document.querySelector(hash); if (el) el.scrollIntoView({ behavior: "smooth" }); }
